import express from "express";
import libraryCardController from "../controllers/libraryCard.controller.js";
import authMiddleware from "../authMiddWare.js";
const router = express.Router();
router.post("/", authMiddleware, libraryCardController.createLibraryCard);
router.get("/", authMiddleware, libraryCardController.getAllLibraryCards);
router.get("/:id", authMiddleware, libraryCardController.getLibraryCardById);
router.put("/:id", authMiddleware, libraryCardController.updateLibraryCard);
router.delete("/:id", authMiddleware, libraryCardController.deleteLibraryCard);
export default router;